import style from 'styled-components';  
import { ButtonSize, ButtonColor } from './Button';

const BaseButtonStyle = style.button`
    width: 100px;
    height: 40px;
    border-radius: 5px;
    border: none;
    cursor: pointer;
    font-size: 16px;
    font-weight: bold;
    color: white;
    background-color: ${props => {
        if (props.buttonColor === ButtonColor.BLUE) {
            return 'blue';
        } else if (props.buttonColor === ButtonColor.WHITE) {
            return 'white';
        } else if (props.buttonColor === ButtonColor.GRAY) {
            return 'gray';
        } else if (props.buttonColor === ButtonColor.RED) {
            return 'red';
        }
    }};
    &:hover {
        opacity: 0.7;
    }
`;

const ModalButton = style(BaseButtonStyle)`
    width: 200px;
    height: 60px;
    font-size: 20px;
`;
